package domain;

public class Offer {

	int id;
	Car car;
	int price;
	
	
	public Offer() {
		// TODO Auto-generated constructor stub
	}


	
	public int getId() {
		return id;
	}


	public void setId(int id) {
		this.id = id;
	}


	public Car getCar() {
		return car;
	}


	public void setCar(Car car) {
		this.car = car;
	}


	public int getPrice() {
		return price;
	}


	public void setPrice(int price) {
		this.price = price;
	}

	
	
}

